import { Observable, BehaviorSubject } from 'rxjs';
import { tap, concatMap } from 'rxjs/operators';
import { Injectable } from '@angular/core';

import {
  AddressLookupService,
  PostcodeLookupAddress,
} from '@wesleyan-frontend/shared/data-access-api';

import { AppStateFacade } from './app-state-facade';

@Injectable({
  providedIn: 'root',
})
export class AddressLookupFacade {
  private addressListSubject$ = new BehaviorSubject<PostcodeLookupAddress[]>(
    []
  );
  addressList$: Observable<
    PostcodeLookupAddress[]
  > = this.addressListSubject$.asObservable();

  constructor(
    private addressLookupService: AddressLookupService,
    private appState: AppStateFacade
  ) {
    if (this.appState.state?.postcodeLookupAddressList) {
      this.addressListSubject$.next(
        this.appState.state.postcodeLookupAddressList
      );
    }

    this.addressList$
      .pipe(
        concatMap((addresses) =>
          this.appState.save({ postcodeLookupAddressList: addresses })
        )
      )
      .subscribe();
  }

  getAddressDetails(id: string) {
    return this.addressLookupService.getAddressDetails(id);
  }

  findByPostcode(postcode: string) {
    return this.addressLookupService.findByPostcode(postcode).pipe(
      tap((response) =>
        response?.addresses
          ? this.addressListSubject$.next(response.addresses)
          : this.addressListSubject$.next([])
      ),
      tap((response) =>
        response?.addresses
          ? this.appState.save({
              postcodeLookupAddressList: response.addresses,
            })
          : this.appState.save({ postcodeLookupAddressList: [] })
      )
    );
  }

  reset() {
    this.addressListSubject$.next([]);
  }
}

import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders, HttpParams } from '@angular/common/http';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';

import {
  PostcodeLookupAddress,
  PostcodeLookupResponse,
  AddressDetails,
} from './address-lookup.interface';

@Injectable({
  providedIn: 'root',
})
export class AddressLookupService {
  constructor(private http: HttpClient) {}

  findByPostcode(postcode: string) {
    return this.http.get<PostcodeLookupResponse | null>(
      `/api/lookup/search?postcode=${postcode}`
    );
  }

  getAddressDetails(id: string) {
    return this.http.get<AddressDetails | null>(
      `/api/lookup/select?moniker=${id}&format=experian`
    );
  }
}

import { Injectable } from '@angular/core';
import { ApplicationInsights } from '@microsoft/applicationinsights-web';

@Injectable({
  providedIn: 'root',
})
export class ApplicationInsightsMonitoringService {
  appInsights: ApplicationInsights;
  private isMonitoringRunning = false;

  constructor() {}

  config(instrumentationKey: string, appName: string) {
    this.appInsights = new ApplicationInsights({
      config: {
        instrumentationKey,
        accountId: `acc-id-${appName}`,
        appId: `app-id-${appName}`,
      },
    });
  }

  startMonitoring() {
    this.appInsights.loadAppInsights();
    this.isMonitoringRunning = true;
  }

  logPageView(name?: string, url?: string) {
    if (this.isMonitoringRunning) {
      // option to call manually
      this.appInsights.trackPageView({
        name: name,
        uri: url,
      });
    }
  }

  logEvent(name: string, properties?: { [key: string]: any }) {
    if (this.isMonitoringRunning) {
      this.appInsights.trackEvent({ name: name }, properties);
    }
  }

  logMetric(
    name: string,
    average: number,
    properties?: { [key: string]: any }
  ) {
    if (this.isMonitoringRunning) {
      this.appInsights.trackMetric(
        { name: name, average: average },
        properties
      );
    }
  }

  logException(exception: Error, severityLevel?: number) {
    if (this.isMonitoringRunning) {
      this.appInsights.trackException({
        exception: exception,
        severityLevel: severityLevel,
      });
    }
  }

  logTrace(message: string, properties?: { [key: string]: any }) {
    if (this.isMonitoringRunning) {
      this.appInsights.trackTrace({ message: message }, properties);
    }
  }
}

