import { distinctUntilChanged, filter, map, tap } from 'rxjs/operators';
import { BehaviorSubject, fromEvent, merge, of } from 'rxjs';
import { Injectable } from '@angular/core';

import { CookieService } from 'ngx-cookie-service';

interface ConsentUpdateEvent extends CustomEvent {
  detail: { cookieName: string };
}

const PERFORMANCE_CONSENT_COOKIE = 'WesleyanPerformanceOptIn';
const FUNCTIONAL_CONSENT_COOKIE = 'WesleyanFunctionalOptIn';

@Injectable({
  providedIn: 'root',
})
export class CookieConsentService {
  private performanceConsentGiven$$ = new BehaviorSubject(false);
  private functionalConsentGiven$$ = new BehaviorSubject(false);

  /**
   * A custom event needs added to Cassie Cookie Manager
   * that should fire when consent is given or not
   * containing the name of cookie being updated
   *
   * i.e
   * document.dispatchEvent(
   *    new CustomEvent("consent-update", {detail:{
   *         cookieName:'WesleyanPerformanceOptIn'
   * }}))
   * */
  private consentUpdateEvent$ = fromEvent<ConsentUpdateEvent>(
    document,
    'consent-update'
  );

  performanceConsentGiven$ = this.performanceConsentGiven$$.asObservable();
  functionalConsentGiven$ = this.functionalConsentGiven$$.asObservable();

  constructor(private cookieService: CookieService) {
    this.checkConsentGiven(
      PERFORMANCE_CONSENT_COOKIE,
      this.performanceConsentGiven$$
    );
    this.checkConsentGiven(
      FUNCTIONAL_CONSENT_COOKIE,
      this.functionalConsentGiven$$
    );
  }

  private checkConsentGiven(cookieName: string, bs: BehaviorSubject<boolean>) {
    merge(
      //Consent already given previously, cookie should be present
      of(this.cookieService.get(cookieName) === 'yes'),
      //Consent modal present on the app, waiting for user consent
      this.consentUpdateEvent$.pipe(
        filter((e) => e.detail.cookieName === cookieName),
        map((v) => this.cookieService.get(cookieName) === 'yes')
      )
    )
      .pipe(
        //If consent already given before the app loads
        //two true values will fire, we need only one
        distinctUntilChanged(),
        tap((v) => bs.next(v))
      )
      .subscribe();
  }
}

import { Injectable } from '@angular/core';
import {
  map,
  take,
  tap,
  finalize,
  concatMapTo,
  concatMap,
} from 'rxjs/operators';
import { Observable, BehaviorSubject, of, timer } from 'rxjs';

import {
  ISAApiService,
  GenericLookupResponse,
  GenericLookup,
  PersonalDetailsDTO,
  CurrentAddressDTO,
  SearchCustomerDTO,
  MarketingPreferencesDTO,
  NationalityDTO,
  CurrentTaxPeriodISALimits,
  CategoryCode,
  ConfigService,
} from '@wesleyan-frontend/wpisa/data-access';
import { OverlayProgressSpinnerService } from '@wesleyan-frontend/shared/ui-progress-spinner';
import { AddressDetails } from '@wesleyan-frontend/shared/data-access-api';

import { GenericDropdownItem } from '../models/generic-dropdown-item.interface';
import { CustomerDetailsFormValue } from '../models/customer-details-form-value.interface';
import { ManualAddressFormValue } from '../../components/manual-address-form/manual-address-form-value.interface';
import { AppStateFacade } from './app-state-facade';
import { NgFormsManager } from '@ngneat/forms-manager';
import { AppForms } from '../models/app-forms.interface';

@Injectable({
  providedIn: 'root',
})
export class CustomerDetailsFacade {
  private submitSubject$ = new BehaviorSubject<boolean>(false);
  private currentTaxPeriodISALimitsSubject$ = new BehaviorSubject<CurrentTaxPeriodISALimits | null>(
    null
  );

  genericLookupsResponse$: Observable<GenericLookupResponse>;
  genericLookups$: Observable<GenericLookup[]>;

  titleList$: Observable<GenericDropdownItem[]>;
  nationalityList$: Observable<GenericDropdownItem[]>;
  marketSegmentList$: Observable<GenericDropdownItem[]>;

  submitSuccessful$: Observable<boolean> = this.submitSubject$.asObservable();
  currentTaxPeriodISALimits$: Observable<
    CurrentTaxPeriodISALimits
  > = this.currentTaxPeriodISALimitsSubject$.asObservable();

  constructor(
    private isaApiService: ISAApiService,
    private appStateFacade: AppStateFacade,
    private loadingService: OverlayProgressSpinnerService,
    private configService: ConfigService,
    private formManager: NgFormsManager<AppForms>
  ) {
    this.genericLookupsResponse$ = this.isaApiService.getGenericListAndProductData();
    this.genericLookups$ = this.genericLookupsResponse$.pipe(
      map((data) => data.data.genericLookups)
    );

    this.titleList$ = of(
      this.configService.content.titleList.map(({ value, label }) => ({
        value,
        description: label,
      }))
    );

    this.nationalityList$ = this.genericLookups$.pipe(
      map((lookups) =>
        lookups.find(
          ({ categoryCode }) => categoryCode === CategoryCode.NATIONALITY
        )
      ),
      map((lookup) => this.mapMembersToSelectList(lookup))
    );

    this.marketSegmentList$ = of(
      this.configService.content.marketSegmentList.map(({ key, label }) => ({
        value: key,
        description: label,
      }))
    );

    this.rehydrateCurrentTaxPeriodFromAppState();
  }

  submit(value: CustomerDetailsFormValue) {
    const customerDTO = this.mapCustomerFormToSearchCustomerDTO(value);
    this.loadingService.show();

    return this.appStateFacade
      .save({
        forms: {
          customerPersonalDetails: value,
          addressLookup: this.formManager.getControl('addressLookup')?.value,
          manualAddress: this.formManager.getControl('manualAddress')?.value,
        },
      })
      .pipe(
        concatMapTo(
          this.isaApiService.findCustomer(customerDTO).pipe(
            tap((resp) =>
              this.currentTaxPeriodISALimitsSubject$.next(
                resp.data.currentTaxPeriod
              )
            ),
            concatMap((resp) =>
              this.appStateFacade.save({
                currentTaxPeriod: resp.data.currentTaxPeriod,
              })
            ),
            tap((_) => this.submitSubject$.next(true)),
            finalize(() => this.loadingService.hide())
          )
        )
      );
  }

  mapCustomerFormToSearchCustomerDTO(
    data: CustomerDetailsFormValue
  ): SearchCustomerDTO {
    return {
      customerDetails: this.mapCustomerDetails(data),
      currentAddress: this.mapCustomerAddress(data),
      nationalityDetails: this.mapCustomerNationality(data),
      marketingPreferences: this.mapCustomerMarketingPreferences(data),
    };
  }

  mapCustomerDetails(value: CustomerDetailsFormValue): PersonalDetailsDTO {
    const mappedData = {
      surname: value.lastName,
      forename: value.firstName,
      emailAddress: value.personalEmail,
      marketSegmentCode: this.getSegmentValueByKey(value.profession),
      dateOfBirth: `${value.dob.year}-${value.dob.month}-${value.dob.day}`,
      mobilePhoneNumber: value.personalMobileNumber.replace(/[^0-9]/g, ''),
      niNumber: value.nationalInsuranceNumber.replace(/[^A-Za-z0-9]/g, ''),
      title: null,
    };

    this.titleList$
      .pipe(
        map(
          (list) => list.find((item) => item.value === value.title).description
        ),
        tap((description) => (mappedData['title'] = description)),
        take(1)
      )
      .subscribe();

    return mappedData;
  }

  mapCustomerAddress(value: CustomerDetailsFormValue): CurrentAddressDTO {
    const mappedData = {
      flat: value.manualAddress.flatNumber,
      houseNumber: value.manualAddress.houseNumber,
      houseName: value.manualAddress.houseName,
      street: value.manualAddress.street,
      district: value.manualAddress.region,
      town: value.manualAddress.town,
      county: value.manualAddress.county,
      postcode: value.manualAddress.postcode,
    };

    return mappedData;
  }

  mapCustomerNationality(value: CustomerDetailsFormValue): NationalityDTO {
    const mappedData = { primaryNationality: null };
    this.nationalityList$
      .pipe(
        map(
          (list) =>
            list.find((item) => item.value === value.nationality).description
        ),
        tap((description) => (mappedData.primaryNationality = description)),
        take(1)
      )
      .subscribe();

    return mappedData;
  }

  mapCustomerMarketingPreferences(
    value: CustomerDetailsFormValue
  ): MarketingPreferencesDTO {
    const mappedData = {
      permitForPost: value.marketingPost,
      permitForEmail: value.marketingEmail,
      permitForTelephone: value.marketingPhone,
    };

    return mappedData;
  }

  mapToManualFormAddress(address: AddressDetails): ManualAddressFormValue {
    return {
      postcode: address.postcode,
      town: address.town,
      county: address.county,
      houseNumber: address.buildingNumber,
      houseName: address.buildingName,
      street: address.street,
      flatNumber: address.flat,
      region: address.district,
    };
  }

  getNationalityById$(id: string) {
    return this.nationalityList$.pipe(
      map((list) => list.find((item) => item.value === id).description)
    );
  }

  getTitleById$(id: string) {
    return this.titleList$.pipe(
      map((list) => list.find((item) => item.value === id).description)
    );
  }

  getProfessionById$(id: string) {
    return this.marketSegmentList$.pipe(
      map((list) => list.find((item) => item.value === id).description)
    );
  }

  getSegmentValueByKey(key: string): string {
    return this.configService.content.marketSegmentList
      .filter((v) => v.key === key)
      .map((v) => v.value)[0];
  }

  private rehydrateCurrentTaxPeriodFromAppState() {
    if (this.appStateFacade.state.currentTaxPeriod) {
      this.currentTaxPeriodISALimitsSubject$.next(
        this.appStateFacade.state.currentTaxPeriod
      );
    }
  }

  private mapMembersToSelectList(
    lookup: GenericLookup
  ): { value: string; description: string }[] {
    return lookup.members.map((member) => ({
      value: member.memberId,
      description: member.customerDescription,
    }));
  }
}

import { HttpErrorResponse } from '@angular/common/http';
import { NgFormsManager } from '@ngneat/forms-manager';
import { Injectable, OnInit } from '@angular/core';
import { Observable, BehaviorSubject, combineLatest, throwError } from 'rxjs';
import {
  map,
  filter,
  tap,
  take,
  startWith,
  catchError,
  concatMap,
  finalize,
  shareReplay,
} from 'rxjs/operators';
import { format } from 'date-fns';

import {
  ISAApiService,
  ConfigService,
  Declaration,
  CurrentTaxPeriodISALimits,
  SubmitTransactionDTO,
  PaymentType,
  DirectDebitType,
  OnlineDirectDebitDetails,
  EndPoints,
} from '@wesleyan-frontend/wpisa/data-access';
import { OverlayProgressSpinnerService } from '@wesleyan-frontend/shared/ui-progress-spinner';

import { CustomerDetailsFacade } from './customer-details.facade';
import { AppForms } from '../models/app-forms.interface';
import { CustomerDetailsFormValue } from '../models/customer-details-form-value.interface';
import { PersonalDetailsViewModel } from '../models/personal-details-view-model.interface';
import { ManualAddressFormValue } from '../../components/manual-address-form/manual-address-form-value.interface';
import { DirectDebitViewModel } from '../models/direct-debit-view-model.interface';
import { DirectDebitFormValue } from '../../components/direct-debit-form/direct-debit-form-value.interface';
import { InvestmentCardViewModel } from '../models/investment-card-view-model.interface';
import { formatCurrencyGBP } from '../util-functions';
import {
  InvestmentOptionPaymentTypeStrings,
  InvestmentOptionPaymentType,
} from '../models/investment-option-form-value.interface';
import { isaRoutesNames } from '../../isa-journey.routes.names';
import { KnowledgeCheckFacade } from './knowledge-check.facade';
import { AppStateFacade } from './app-state-facade';
import { AppState } from '../models/app-state.interface';

@Injectable({
  providedIn: 'root',
})
export class DeclarationFacade {
  private endPoints: EndPoints;
  private pageContent: Declaration;
  private pageContentSubject$ = new BehaviorSubject<Declaration>(null);
  private appState: AppState;
  private paymentUrlSubject$ = new BehaviorSubject<string>(null);

  paymentUrl$ = this.paymentUrlSubject$.asObservable();
  pageContent$ = this.pageContentSubject$.asObservable();
  selectedInvestmentOption$: Observable<InvestmentOptionPaymentTypeStrings>;
  personalDetailsViewModelData$: Observable<PersonalDetailsViewModel>;
  directDebitViewModelData$: Observable<DirectDebitViewModel>;
  investmentCardViewModelData$: Observable<InvestmentCardViewModel>;
  lumpSumAmount$: Observable<number>;
  monthlyAmount$: Observable<number>;

  constructor(
    private isaApiService: ISAApiService,
    private configService: ConfigService,
    private appStateFacade: AppStateFacade,
    private customerDetailsFacade: CustomerDetailsFacade,
    private knowledgeCheckFacade: KnowledgeCheckFacade,
    private formManager: NgFormsManager<AppForms>,
    private loadingService: OverlayProgressSpinnerService
  ) {
    this.pageContent = this.configService.content.declaration;
    this.pageContentSubject$.next(this.pageContent);

    this.endPoints = this.configService.content.endPoints;
    this.appState = this.appStateFacade.state;

    this.personalDetailsViewModelData$ = this.formManager
      .valueChanges('customerPersonalDetails')
      .pipe(
        startWith(this.appState.forms?.customerPersonalDetails),
        map((customerPersonalDetailsFormValue) => {
          return this.mapPersonalDetailsFormValuesToViewModel(
            customerPersonalDetailsFormValue
          );
        })
      );

    this.selectedInvestmentOption$ = this.formManager
      .valueChanges('investmentOptions', 'investmentOption')
      .pipe(
        startWith(this.appState.forms.investmentOptions.investmentOption),
        shareReplay(1)
      );

    this.directDebitViewModelData$ = combineLatest([
      this.formManager
        .valueChanges('monthlyPayment', 'directDebit')
        .pipe(startWith(this.appState.forms?.monthlyPayment?.directDebit)),
      this.formManager
        .valueChanges('lumpSumAndMonthly', 'directDebit')
        .pipe(startWith(this.appState.forms?.lumpSumAndMonthly?.directDebit)),
      this.selectedInvestmentOption$,
    ]).pipe(
      map(([monthlyPaymentDD, lumpSumAndMonthlyDD, investmentOption]) => {
        const dd =
          investmentOption === InvestmentOptionPaymentType.MONTHLY
            ? monthlyPaymentDD
            : lumpSumAndMonthlyDD;

        return investmentOption === InvestmentOptionPaymentType.LUMP_SUM
          ? null
          : this.mapDirectDebitFormValuesToViewModel(dd, investmentOption);
      })
    );

    this.lumpSumAmount$ = combineLatest([
      this.formManager
        .valueChanges('lumpSumPayment', 'amount')
        .pipe(startWith(this.appState.forms?.lumpSumPayment?.amount)),
      this.formManager
        .valueChanges('lumpSumAndMonthly', 'totalAmount.lumpSumAmount')
        .pipe(
          startWith(
            this.appState.forms?.lumpSumAndMonthly?.totalAmount.lumpSumAmount
          )
        ),
      this.selectedInvestmentOption$.pipe(
        startWith(this.appState.forms.investmentOptions.investmentOption)
      ),
    ]).pipe(
      filter(
        ([
          lumpSumPaymentAmount,
          lumpSumAndMonthlyLumpSumAmount,
          investmentOption,
        ]) =>
          investmentOption === InvestmentOptionPaymentType.LUMP_SUM ||
          investmentOption === InvestmentOptionPaymentType.MONTHLY_AND_LUMP_SUM
      ),
      map(
        ([
          lumpSumPaymentAmount,
          lumpSumAndMonthlyLumpSumAmount,
          investmentOption,
        ]) =>
          investmentOption === InvestmentOptionPaymentType.LUMP_SUM
            ? lumpSumPaymentAmount
            : lumpSumAndMonthlyLumpSumAmount
      )
    );

    this.monthlyAmount$ = combineLatest([
      this.formManager
        .valueChanges('monthlyPayment', 'amount')
        .pipe(startWith(this.appState.forms?.monthlyPayment?.amount)),
      this.formManager
        .valueChanges('lumpSumAndMonthly', 'totalAmount.monthlyAmount')
        .pipe(
          startWith(
            this.appState.forms?.lumpSumAndMonthly?.totalAmount.monthlyAmount
          )
        ),
      this.selectedInvestmentOption$.pipe(
        startWith(this.appState.forms.investmentOptions.investmentOption)
      ),
    ]).pipe(
      filter(
        ([
          monthlyPaymentAmount,
          lumpSumAndMonthlyMonthlyAmount,
          investmentOption,
        ]) =>
          investmentOption === InvestmentOptionPaymentType.MONTHLY ||
          investmentOption === InvestmentOptionPaymentType.MONTHLY_AND_LUMP_SUM
      ),
      map(
        ([
          monthlyPaymentAmount,
          lumpSumAndMonthlyMonthlyAmount,
          investmentOption,
        ]) =>
          investmentOption === InvestmentOptionPaymentType.MONTHLY
            ? monthlyPaymentAmount
            : lumpSumAndMonthlyMonthlyAmount
      )
    );

    this.investmentCardViewModelData$ = combineLatest([
      this.lumpSumAmount$.pipe(startWith(0)),
      this.monthlyAmount$.pipe(startWith(0)),
      this.customerDetailsFacade.currentTaxPeriodISALimits$,
      this.selectedInvestmentOption$,
    ]).pipe(
      map(
        ([
          lumpSumAmount,
          monthlyAmount,
          isaLimits,
          selectedInvestmentOption,
        ]) => ({
          showLumpSum:
            selectedInvestmentOption === InvestmentOptionPaymentType.MONTHLY
              ? false
              : true,
          showMonthly:
            selectedInvestmentOption === InvestmentOptionPaymentType.LUMP_SUM
              ? false
              : true,
          lumpSumPayment: {
            label: 'Lump-sum amount',
            value:
              selectedInvestmentOption === InvestmentOptionPaymentType.MONTHLY
                ? '—'
                : `${formatCurrencyGBP(lumpSumAmount)}`,
          },
          monthlyPayment: {
            label: 'Monthly payments',
            value:
              selectedInvestmentOption === InvestmentOptionPaymentType.LUMP_SUM
                ? '—'
                : `${formatCurrencyGBP(monthlyAmount)}`,
          },
          total: {
            label: this.formatTaxYear(
              this.pageContent.totalInvestmentText,
              isaLimits
            ),
            value: `${formatCurrencyGBP(
              this.calculateTotalInvestment(
                lumpSumAmount,
                monthlyAmount,
                isaLimits.numberOfMonthlyPayments,
                selectedInvestmentOption
              )
            )}`,
          },
          smallCopy: this.formatTotalAnnualAllowance(
            this.pageContent.totalInvestmentSmallText,
            isaLimits.totalAnnualAllowance
          ),
        })
      )
    );
  }

  mapDirectDebitFormValuesToViewModel(
    formValue: DirectDebitFormValue,
    selectedInvestmentOption: InvestmentOptionPaymentTypeStrings
  ): DirectDebitViewModel {
    return {
      showDD:
        selectedInvestmentOption === InvestmentOptionPaymentType.LUMP_SUM
          ? false
          : true,
      accountNumber: {
        label: 'Account number',
        value: formValue.accountNumber,
      },
      sortCode: {
        label: 'Sort code',
        value: `${formValue.sortCode.c1}-${formValue.sortCode.c2}-${formValue.sortCode.c3}`,
      },
    };
  }

  mapPersonalDetailsFormValuesToViewModel(
    formValue: CustomerDetailsFormValue
  ): PersonalDetailsViewModel {
    const {
      title: titleId,
      firstName,
      lastName,
      dob,
      nationality: nationalityId,
      personalEmail,
      profession: professionId,
      personalMobileNumber,
      manualAddress,
    } = formValue;

    const data = {
      name: {
        label: 'Name',
        value: `${titleId} ${firstName} ${lastName}`,
      },
      dob: {
        label: 'Date of birth',
        value: format(
          new Date(
            parseInt(dob.year),
            parseInt(dob.month) - 1, // starts with 0 - January
            parseInt(dob.day)
          ),

          'dd/MM/yyyy'
        ),
      },
      profession: { label: 'Profession', value: professionId },
      nationality: { label: 'Nationality', value: nationalityId },
      emailAddress: { label: 'Email address', value: personalEmail },
      phoneNumber: {
        label: 'Phone number',
        value: personalMobileNumber.replace(/[^0-9]/g, ''),
      },
      homeAddress: {
        label: 'Home address',
        value: this.transformAddress(manualAddress),
      },
    };

    this.customerDetailsFacade
      .getTitleById$(titleId)
      .pipe(
        tap((title) => (data.name.value = `${title} ${firstName} ${lastName}`)),
        take(1)
      )
      .subscribe();

    this.customerDetailsFacade
      .getProfessionById$(professionId)
      .pipe(
        tap((profession) => (data.profession.value = profession)),
        take(1)
      )
      .subscribe();

    this.customerDetailsFacade
      .getNationalityById$(nationalityId)
      .pipe(
        tap((nationality) => (data.nationality.value = nationality)),
        take(1)
      )
      .subscribe();

    return data;
  }

  submitMonthlyISA() {
    this.loadingService.show();

    return this.isaApiService.getTransactionId().pipe(
      map((result) => result.data.transactionId),
      concatMap((transactionId) => {
        const dto = this.getMonthlyTransactionDTO(transactionId);

        return this.isaApiService.submitTransaction(dto).pipe(
          tap((_) => this.loadingService.hide()),
          tap((_) => window.open(this.endPoints.confirmationPage, '_self')),
          catchError((err) => {
            this.handleHttpError(err);

            return throwError(err);
          })
        );
      }),
      catchError((err) => {
        this.handleHttpError(err);

        return throwError(err);
      })
    );
  }

  submitLumpSumISA() {
    this.loadingService.show();

    return this.isaApiService.getTransactionId().pipe(
      map((result) => result.data.transactionId),
      concatMap((transactionId) => {
        const dto = this.getLumpSumTransactionDTO(transactionId);

        return this.isaApiService.getPaymentUrl(dto).pipe(
          tap(() => this.loadingService.reset()),
          tap((data) => {
            this.paymentUrlSubject$.next(data.paymentUrl);
          }),
          catchError((err) => {
            this.handleHttpError(err);

            return throwError(err);
          })
        );
      }),
      catchError((err) => {
        this.handleHttpError(err);

        return throwError(err);
      }),
      finalize(() => this.loadingService.hide())
    );
  }

  submitLumpSumAndMonthlyISA() {
    this.loadingService.show();

    return this.isaApiService.getTransactionId().pipe(
      map((result) => result.data.transactionId),
      concatMap((transactionId) => {
        const dto = this.getLumpSumAndMonthlyTransactionDTO(transactionId);

        return this.isaApiService.getPaymentUrl(dto).pipe(
          tap(() => this.loadingService.reset()),
          tap((data) => {
            this.paymentUrlSubject$.next(data.paymentUrl);
          }),
          tap((_) => this.loadingService.hide()),
          catchError((err) => {
            this.handleHttpError(err);

            return throwError(err);
          })
        );
      }),
      catchError((err) => {
        this.handleHttpError(err);

        return throwError(err);
      }),
      finalize(() => this.loadingService.hide())
    );
  }

  private handleHttpError(err: HttpErrorResponse) {
    if (err.status === 400) {
      window.open(this.endPoints.confirmationErrorPage, '_self');
    }
  }

  getMonthlyTransactionDTO(
    transactionId: string
  ): Omit<SubmitTransactionDTO, 'lumpPaymentDetails' | 'lumpAmount'> {
    const onlineDirectDebitDetails = this.mapDirectDebit(
      this.formManager.getControl('monthlyPayment', 'directDebit')?.value ||
        this.appState.forms.monthlyPayment.directDebit
    );
    const regularAmount =
      this.formManager.getControl('monthlyPayment', 'amount')?.value ||
      this.appState.forms.monthlyPayment.amount;

    const dto = {
      ...this.getCommonSubmitTransactionDTO(transactionId),
      paymentType: PaymentType.Regular,
      regularAmount,
      directDebitType: DirectDebitType.ONLINE,
      onlineDirectDebitDetails,
    };

    return dto;
  }

  getLumpSumTransactionDTO(
    transactionId: string
  ): Omit<
    SubmitTransactionDTO,
    'directDebitType' | 'regularAmount' | 'onlineDirectDebitDetails'
  > {
    const lumpAmount =
      this.formManager.getControl('lumpSumPayment', 'amount')?.value ||
      this.appState.forms.lumpSumPayment.amount;

    const dto = {
      ...this.getCommonSubmitTransactionDTO(transactionId),
      paymentType: PaymentType.Lump,
      lumpAmount,
    };

    return dto;
  }

  getLumpSumAndMonthlyTransactionDTO(
    transactionId: string
  ): SubmitTransactionDTO {
    const lumpAmount =
      this.formManager.getControl(
        'lumpSumAndMonthly',
        'totalAmount.lumpSumAmount'
      )?.value ||
      this.appState.forms.lumpSumAndMonthly.totalAmount.lumpSumAmount;

    const regularAmount =
      this.formManager.getControl(
        'lumpSumAndMonthly',
        'totalAmount.monthlyAmount'
      )?.value ||
      this.appState.forms.lumpSumAndMonthly.totalAmount.monthlyAmount;

    const onlineDirectDebitDetails = this.mapDirectDebit(
      this.formManager.getControl('lumpSumAndMonthly', 'directDebit')?.value ||
        this.appState.forms.lumpSumAndMonthly.directDebit
    );

    const dto = {
      ...this.getCommonSubmitTransactionDTO(transactionId),
      paymentType: PaymentType.Both,
      lumpAmount,
      regularAmount,
      directDebitType: DirectDebitType.ONLINE,
      onlineDirectDebitDetails,
    };

    return dto;
  }

  getCommonSubmitTransactionDTO(
    transactionId: string
  ): Pick<
    SubmitTransactionDTO,
    | 'appTestAttemptId'
    | 'transactionId'
    | 'taxPeriodCode'
    | 'customerPermissionGranted'
    | 'currentAddress'
    | 'customerDetails'
    | 'nationalityDetails'
    | 'marketingPreferences'
    | 'declarationsText'
    | 'knowledgeCheckForm'
  > {
    const mappedCustomerDTO = this.customerDetailsFacade.mapCustomerFormToSearchCustomerDTO(
      this.formManager.getControl('customerPersonalDetails')?.value ||
        this.appState.forms.customerPersonalDetails
    );

    const dto = {
      currentAddress: mappedCustomerDTO.currentAddress,
      customerDetails: mappedCustomerDTO.customerDetails,
      nationalityDetails: mappedCustomerDTO.nationalityDetails,
      marketingPreferences: mappedCustomerDTO.marketingPreferences,
      transactionId: transactionId,
      appTestAttemptId: this.knowledgeCheckFacade.knowledgeCheckAttemptId,
      taxPeriodCode: '',
      customerPermissionGranted: true,
      declarationsText: (
        this.pageContent.declarationText + this.pageContent.confirmContent
      ).replace(/<[^>]*>/g, ''),
      knowledgeCheckForm: this.getKnowledgeCheckAnswersDTO(),
    };

    this.customerDetailsFacade.currentTaxPeriodISALimits$
      .pipe(
        tap((value) => (dto.taxPeriodCode = value.taxPeriodCode)),
        take(1)
      )
      .subscribe();
    return dto;
  }

  getKnowledgeCheckAnswersDTO() {
    return {
      knowledgeCheckQ1: this.appState.forms.knowledgeCheckQ1,
      knowledgeCheckQ2: this.appState.forms.knowledgeCheckQ2,
    };
  }

  mapDirectDebit(value: DirectDebitFormValue): OnlineDirectDebitDetails {
    return {
      accountHolderFullName: value.accountHolderFullName,
      sortCode: `${value.sortCode.c1}-${value.sortCode.c2}-${value.sortCode.c3}`,
      accountNumber: value.accountNumber,
      bankName: value.bankName,
      customerConfirmedDD: true,
      accountValidated: false,
    };
  }

  calculateTotalInvestment(
    lumpSumAmount: number = 0,
    monthlyAmount: number,
    numberOfMonthlyPayments: number,
    selectedInvestmentOption: InvestmentOptionPaymentTypeStrings
  ): number {
    switch (selectedInvestmentOption) {
      case InvestmentOptionPaymentType.LUMP_SUM:
        return parseFloat('' + lumpSumAmount);
        break;

      case InvestmentOptionPaymentType.MONTHLY:
        return (
          parseFloat('' + monthlyAmount) *
          parseInt('' + numberOfMonthlyPayments)
        );
        break;

      default:
        return (
          parseFloat('' + lumpSumAmount) +
          parseFloat('' + monthlyAmount) *
            parseInt('' + numberOfMonthlyPayments)
        );
        break;
    }
  }

  transformAddress(address: ManualAddressFormValue, delimiter = ', '): string {
    if (!address) {
      return '';
    }

    const fields: { fieldName: string; delimited: boolean }[] = [
      { fieldName: 'flatNumber', delimited: false },
      { fieldName: 'houseNumber', delimited: false },
      { fieldName: 'houseName', delimited: true },
      { fieldName: 'street', delimited: true },
      { fieldName: 'town', delimited: true },
      { fieldName: 'region', delimited: true },
      { fieldName: 'county', delimited: true },
      { fieldName: 'postcode', delimited: true },
    ];

    let result = fields.reduce((accumulator, field) => {
      const value = address[field.fieldName];

      return value
        ? `${accumulator}${value}${field.delimited ? delimiter : ' '}`
        : accumulator;
    }, '');

    result = result.substr(0, result.length - delimiter.length);

    return result;
  }

  formatTaxYear(text: string, isaLimits: CurrentTaxPeriodISALimits): string {
    return text.replace(
      '{tax-year}',
      format(new Date(isaLimits.startDateTime), 'yyyy') +
        '/' +
        format(new Date(isaLimits.endDateTime), 'yyyy')
    );
  }

  formatTotalAnnualAllowance(text: string, amount: number) {
    return text.replace('{total-annual-allowance}', formatCurrencyGBP(amount));
  }
}

import { Injectable } from '@angular/core';
import { throwError, Subject } from 'rxjs';
import { Router } from '@angular/router';
import { tap, take, catchError } from 'rxjs/operators';
import { HttpErrorResponse } from '@angular/common/http';

import {
  ConfigService,
  FindFCApiService,
} from '@wesleyan-frontend/find-my-fc/data-access';
import { OverlayProgressSpinnerService } from '@wesleyan-frontend/shared/ui-progress-spinner';
import { routesNames } from '@wesleyan-frontend/find-my-fc/util-const';

const NO_FINANCIAL_CONSULTANT_FOUND = 404;
const ALLOWED_RETRIES = 3;

@Injectable({
  providedIn: 'root',
})
export class FinancialConsultantFacade {
  private invalidCustomerReferenceCount = 0;
  private invalidCustomerReference$$ = new Subject<boolean>();

  invalidCustomerReference$ = this.invalidCustomerReference$$.asObservable();

  constructor(
    private configService: ConfigService,
    private findFinancialConsultantService: FindFCApiService,
    private loadingService: OverlayProgressSpinnerService,
    private router: Router
  ) {}

  findFCByCustomerReference(customerReference: string) {
    this.loadingService.show();

    this.findFinancialConsultantService
      .findByCustomerID(customerReference)
      .pipe(
        tap((_) => this.loadingService.hide()),
        tap((response) => this.navigateToFinancialConsultantPage(response.id)),
        take(1),
        catchError((err: HttpErrorResponse) => {
          this.loadingService.hide();
          this.handleError(err);

          return throwError(err);
        })
      )
      .subscribe();
  }

  findFCByCustomerDetails(
    dateOfBirth: string,
    emailAddress: string,
    postcode: string
  ) {
    this.loadingService.show();

    this.findFinancialConsultantService
      .findByCustomerDetails(dateOfBirth, emailAddress, postcode)
      .pipe(
        tap((_) => this.loadingService.hide()),
        tap((response) => this.navigateToFinancialConsultantPage(response.id)),
        take(1),
        catchError((err: HttpErrorResponse) => {
          this.loadingService.hide();
          this.handleError(err);

          return throwError(err);
        })
      )
      .subscribe();
  }

  findFCByPostcodeAndSegment(postcode: string, segment: string) {
    this.loadingService.show();

    this.findFinancialConsultantService
      .findByFCDetails(postcode, segment)
      .pipe(
        tap((_) => this.loadingService.hide()),
        tap((response) => this.navigateToFinancialConsultantPage(response.id)),
        take(1),
        catchError((err: HttpErrorResponse) => {
          this.loadingService.hide();
          this.handleError(err);

          return throwError(err);
        })
      )
      .subscribe();
  }

  resetRetryCounter() {
    this.invalidCustomerReferenceCount = 0;
  }

  navigateToFinancialConsultantPage(consultantId: number) {
    window.open(
      `${this.configService.content.endPoints.yourFinancialConsultant}`,
      '_self'
    );
  }

  private handleError(err: HttpErrorResponse) {
    if (err.status === NO_FINANCIAL_CONSULTANT_FOUND) {
      this.invalidCustomerReference$$.next(true);
      this.invalidCustomerReferenceCount++;
    }

    if (this.invalidCustomerReferenceCount === ALLOWED_RETRIES) {
      this.router.navigate([routesNames.CANNOT_FIND_CUSTOMER]);
    }
  }
}

import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

import { FindConsultantResponse } from './find-fc-api.interface';

@Injectable({
  providedIn: 'root',
})
export class FindFCApiService {
  constructor(private http: HttpClient) {}

  findByFCDetails(
    postcode: string,
    segment: string
  ): Observable<FindConsultantResponse> {
    return this.http.post<FindConsultantResponse>(
      `/api/findmyfcwebapiwrapper/newcustomersearch`,
      { segment, postcode }
    );
  }

  findByCustomerID(customerId: string): Observable<FindConsultantResponse> {
    return this.http.get<FindConsultantResponse>(
      `/api/findmyfcwebapiwrapper/existingcustomersearch?id=${customerId}`
    );
  }

  /**
   *
   * @param dateOfBirth in yyyy-mm-dd format ie. 1950-15-01
   * @param emailAddress
   * @param postcode
   */
  findByCustomerDetails(
    dateOfBirth: string,
    emailAddress: string,
    postcode: string
  ): Observable<FindConsultantResponse> {
    return this.http.post<FindConsultantResponse>(
      `/api/findmyfcwebapiwrapper/existingcustomersearch`,
      { dateOfBirth, postcode, emailAddress }
    );
  }
}

