import { Inject, Injectable, Optional } from '@angular/core';
import { GoogleTagManagerConfig } from './google-tag-manager-config';

@Injectable({
  providedIn: 'root',
})
export class GoogleTagManagerService {
  private isLoaded = false;

  private browserGlobals = {
    windowRef(): any {
      return window;
    },
    documentRef(): any {
      return document;
    },
  };

  constructor(
    @Optional()
    @Inject('googleTagManagerConfig')
    public config: GoogleTagManagerConfig = { id: null },
    @Optional() @Inject('googleTagManagerId') public googleTagManagerId: string,
    @Optional()
    @Inject('googleTagManagerAuth')
    public googleTagManagerAuth: string,
    @Optional()
    @Inject('googleTagManagerPreview')
    public googleTagManagerPreview: string
  ) {
    if (this.config == null) {
      this.config = { id: null };
    }

    this.config = {
      ...this.config,
      id: googleTagManagerId || this.config.id,
      gtm_auth: googleTagManagerAuth || this.config['gtm_auth'],
      gtm_preview: googleTagManagerPreview || this.config['gtm_preview'],
    };
    if (this.config.id == null) {
      throw new Error('Google tag manager ID not provided.');
    }
  }

  public getDataLayer() {
    const window = this.browserGlobals.windowRef();
    window['dataLayer'] = window['dataLayer'] || [];
    return window['dataLayer'];
  }

  private pushOnDataLayer(obj: object) {
    const dataLayer = this.getDataLayer();
    dataLayer.push(obj);
  }

  public addGtmToDom() {
    if (this.isLoaded) {
      return;
    }
    const doc = this.browserGlobals.documentRef();
    this.pushOnDataLayer({
      'gtm.start': new Date().getTime(),
      event: 'gtm.js',
      pagePath: window.location.pathname + window.location.hash,
    });

    const gtmScript = doc.createElement('script');
    gtmScript.id = 'GTMscript';
    gtmScript.async = true;
    gtmScript.src = this.applyGtmQueryParams(
      'https://www.googletagmanager.com/gtm.js'
    );
    doc.head.insertBefore(gtmScript, doc.head.firstChild);

    // const ifrm = doc.createElement('iframe');
    // ifrm.setAttribute(
    //   'src',
    //   this.applyGtmQueryParams('https://www.googletagmanager.com/ns.html')
    // );
    // ifrm.style.width = '0';
    // ifrm.style.height = '0';
    // ifrm.style.display = 'none';
    // ifrm.style.visibility = 'hidden';

    // const noscript = doc.createElement('noscript');
    // noscript.id = 'GTMiframe';
    // noscript.appendChild(ifrm);
    // doc.body.insertBefore(noscript, doc.body.firstChild);

    this.isLoaded = true;
  }

  public pushTag(item: object) {
    if (!this.isLoaded) {
      this.addGtmToDom();
    }
    this.pushOnDataLayer(item);
  }

  public gtmScriptsLoadedExternally() {
    this.isLoaded = true;
  }

  private applyGtmQueryParams(url: string) {
    if (url.indexOf('?') === -1) {
      url += '?';
    }

    return (
      url +
      Object.keys(this.config)
        .filter((k) => this.config[k])
        .map((k) => `${k}=${this.config[k]}`)
        .join('&')
    );
  }
}

import { Idle, DEFAULT_INTERRUPTSOURCES } from '@ng-idle/core';
import { startWith, finalize, take } from 'rxjs/operators';
import { MatDialog } from '@angular/material/dialog';
import { Injectable } from '@angular/core';

import {
  SessionStorageService,
  ConfigService,
} from '@wesleyan-frontend/wpisa/data-access';

import { InactivityModalComponent } from './inactivity-modal/inactivity-modal.component';

@Injectable({
  providedIn: 'root',
})
export class InactivityTimeoutService {
  timedOut = false;
  private redirectUrl = '/';
  private idleTimeInSeconds = 9 * 60;
  private timeoutInSeconds = 60;

  constructor(
    private idle: Idle,
    private dialog: MatDialog,
    private sessionService: SessionStorageService,
    private configService: ConfigService
  ) {
    this.redirectUrl = this.configService.content.endPoints.bookmarkRedirectPage;
    this.idleTimeInSeconds =
      this.configService.content.inactivityModal.idleTimeInSeconds || 9 * 60;
    this.timeoutInSeconds =
      this.configService.content.inactivityModal.timeoutTimeInSeconds || 60;
  }

  initInactivityTimeout() {
    // Sets the idle timeout of 9 minutes
    this.idle.setIdle(this.idleTimeInSeconds);
    this.idle.setIdleName('inactivityTimeout');
    // Sets a timeout period of 60 seconds
    this.idle.setTimeout(this.timeoutInSeconds);

    this.idle.onTimeout.subscribe(() => {
      this.timedOut = true;
      this.sessionService
        .clear()
        .pipe(finalize(() => window.open(this.redirectUrl, '_self')))
        .subscribe();
    });

    this.idle.onIdleStart.subscribe(() => {
      //We don't want to interrupt the countdown in modal
      this.idle.clearInterrupts();

      const dialogRef = this.dialog.open(InactivityModalComponent, {
        panelClass: ['wes-modal', 'wes-inactivity-modal'],
        data: {
          countDown: this.idle.onTimeoutWarning.pipe(
            startWith(this.timeoutInSeconds)
          ),
        },
      });

      dialogRef
        .afterClosed()
        .pipe(take(1))
        .subscribe((result) => {
          this.reset();
        });
    });

    this.reset();
  }

  reset() {
    // Sets the default interrupts, in this case, things like
    // clicks, scrolls, touches to the document
    this.idle.setInterrupts(DEFAULT_INTERRUPTSOURCES);
    this.idle.watch();
    this.timedOut = false;
  }
}

