import { tap, catchError, concatMapTo, concatMap } from 'rxjs/operators';
import { HttpErrorResponse } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { throwError } from 'rxjs';

import { OverlayProgressSpinnerService } from '@wesleyan-frontend/shared/ui-progress-spinner';
import { ISAApiService } from '@wesleyan-frontend/wpisa/data-access';

import { isaRoutesNames } from '../../isa-journey.routes.names';
import { AppStateFacade } from './app-state-facade';

@Injectable({
  providedIn: 'root',
})
export class KnowledgeCheckFacade {
  knowledgeCheckAttemptId: number;

  constructor(
    private isaApiService: ISAApiService,
    private appState: AppStateFacade,
    private loadingService: OverlayProgressSpinnerService
  ) {
    this.knowledgeCheckAttemptId = this.appState.state.attemptId;
  }

  submitQuestion1(
    answerValue: 'Yes' | 'No',
    answerLabel: string,
    questionText: string
  ) {
    this.loadingService.show();

    const kcAnswers = {
      forms: {
        knowledgeCheckQ1: { question1: answerValue },
        knowledgeCheckQ2: { question2: null }, //Reset question 2
      },
    };

    return this.appState.save(kcAnswers).pipe(
      concatMapTo(
        this.isaApiService
          .submitInitialAnswer({
            questionIndex: 1,
            questionText,
            answerText: answerLabel,
            answerValue,
          })
          .pipe(
            tap(
              (response) =>
                (this.knowledgeCheckAttemptId = response.data.attemptId)
            ),
            concatMap((response) =>
              this.appState
                .save({ attemptId: response.data.attemptId })
                .pipe(tap((_) => this.loadingService.hide()))
            ),
            catchError((err) => {
              this.handleHttpError(err);

              return throwError(err);
            })
          )
      )
    );
  }

  submitQuestion2(
    answerValue: 'Yes' | 'No',
    answerLabel: string,
    questionText: string
  ) {
    this.loadingService.show();

    return this.appState.saveFormState('knowledgeCheckQ2').pipe(
      concatMapTo(
        this.isaApiService
          .submitSubsequentAnswer({
            attemptId: this.knowledgeCheckAttemptId,
            questionIndex: 2,
            questionText,
            answerText: answerLabel,
            answerValue,
          })
          .pipe(
            tap((_) => this.loadingService.hide()),
            catchError((err) => {
              this.handleHttpError(err);

              return throwError(err);
            })
          )
      )
    );
  }

  private handleHttpError(err: HttpErrorResponse) {
    if (err.status === 400) {
      window.open(`/${isaRoutesNames.KNOWLEDGE_CHECK_ERROR}`, '_self');
    }
  }
}

import { Injectable } from '@angular/core';
import { Overlay, OverlayRef } from '@angular/cdk/overlay';
import { LiveAnnouncer } from '@angular/cdk/a11y';
import { ComponentPortal } from '@angular/cdk/portal';
import { Observable, Subject } from 'rxjs';
import { mapTo, scan, map, distinctUntilChanged } from 'rxjs/operators';

import { ProgressSpinnerComponent } from './progress-spinner/progress-spinner.component';

@Injectable({
  providedIn: 'root',
})
export class OverlayProgressSpinnerService {
  private spinnerTopRef: OverlayRef;

  private spin$: Subject<number> = new Subject();

  constructor(private overlay: Overlay, private liveAnnouncer: LiveAnnouncer) {
    this.spinnerTopRef = this.overlay.create({
      hasBackdrop: true,
      backdropClass: 'wes-loading-overlay-light-backdrop',
      positionStrategy: this.overlay
        .position()
        .global()
        .centerHorizontally()
        .centerVertically(),
    });

    this.spin$
      .asObservable()
      .pipe(
        scan((acc, next) => {
          if (!next) return 0;
          return acc + next >= 0 ? acc + next : 0;
        }, 0),
        map((val) => val > 0),
        distinctUntilChanged()
      )
      .subscribe((res) => {
        if (res) {
          this.spinnerTopRef.attach(
            new ComponentPortal(ProgressSpinnerComponent)
          );
        } else if (this.spinnerTopRef.hasAttached()) {
          this.spinnerTopRef.detach();
        }
      });
  }
  show() {
    this.spin$.next(1);
    this.liveAnnouncer.announce('Loading, please wait');
  }
  hide() {
    this.spin$.next(-1);
    this.liveAnnouncer.clear();
  }
  reset() {
    this.spin$.next(0);
  }
}

