import {
  Directive,
  ElementRef,
  Input,
  Renderer2,
  HostBinding,
  AfterViewInit,
} from '@angular/core';

@Directive({
  selector: '[wesButtonLoader]',
})
export class ButtonLoaderDirective implements AfterViewInit {
  spinnerElement: HTMLSpanElement;
  textPlaceholder: HTMLSpanElement;
  savedText: string;
  isLoading = false;

  @Input() set wesButtonLoader(value: boolean) {
    this.isLoading = value;
    this.toggle(value);
  }

  @HostBinding('class.wes-button--loading') get loading() {
    return this.isLoading;
  }

  constructor(
    private element: ElementRef<HTMLElement>,
    private renderer: Renderer2
  ) {
    this.toggle(this.wesButtonLoader);
  }

  ngAfterViewInit() {
    this.moveButtonTextInWrapper();
  }

  toggle(condition: boolean) {
    condition ? this.show() : this.hide();
  }

  show() {
    this.spinnerElement = this.renderer.createElement('span');
    this.renderer.addClass(this.spinnerElement, 'wes-spinner');
    this.renderer.addClass(this.spinnerElement, 'wes-spinner--button');

    // Set the button to maintain the same dimensions, even once we put the spinner inside.
    this.element.nativeElement.style.width = `${
      (this.element.nativeElement as HTMLElement).offsetWidth
    }px`;
    this.element.nativeElement.style.height = `${
      (this.element.nativeElement as HTMLElement).offsetHeight
    }px`;

    if (this.spinnerElement) {
      this.renderer.appendChild(
        this.element.nativeElement,
        this.spinnerElement
      );
    }
  }

  hide() {
    if (this.spinnerElement) {
      this.renderer.removeChild(
        this.element.nativeElement,
        this.spinnerElement
      );
    }
  }

  private moveButtonTextInWrapper() {
    this.textPlaceholder = this.renderer.createElement('span');
    this.renderer.addClass(this.textPlaceholder, 'wes-button-text');
    this.renderer.appendChild(
      this.textPlaceholder,
      this.renderer.createText(this.element.nativeElement.innerText)
    );
    this.element.nativeElement.innerText = '';
    this.renderer.appendChild(this.element.nativeElement, this.textPlaceholder);
  }
}

import { Directive, ElementRef, HostListener } from '@angular/core';

/**
 * Clear the input on click, restore initial value if no change
 */
@Directive({
  selector: '[wesClearRestoreInputOnFocus]',
})
export class ClearRestoreInputOnFocusDirective {
  constructor(private _el: ElementRef) {}
  initialValue: string;

  @HostListener('focus', ['$event']) onInputClick(event) {
    this.initialValue = this._el.nativeElement.value;

    //Clear the input
    this._el.nativeElement.value = '';
  }

  @HostListener('blur', ['$event']) onInputBlur(event) {
    const currentValue = this._el.nativeElement.value;

    // restore original if no change
    if (currentValue === '') {
      this._el.nativeElement.value = this.initialValue;
    }
  }
}

import { Directive, ElementRef, HostListener } from '@angular/core';
import { DefaultValueAccessor, NgControl } from '@angular/forms';

/**
 * Hack for Angular bug happening when pressing enter on a updateOn:blur
 * control, the wrong value is passed to validators
 * https://github.com/angular/angular/issues/26683#issuecomment-646961591
 */
@Directive({
  // https://github.com/angular/angular/blob/master/packages/forms/src/directives/default_value_accessor.ts#L63
  selector:
    'input:not([type=checkbox])[formControlName],textarea[formControlName],input:not([type=checkbox])[formControl],textarea[formControl],input:not([type=checkbox])[ngModel],textarea[ngModel],[ngDefaultControl]',
})
export class ControlUpdateOnBlurOrChangeDirective {
  constructor(private elementRef: ElementRef, private ngControl: NgControl) {}

  @HostListener('input')
  onInput() {
    if (this.elementRef.nativeElement !== document.activeElement) {
      this.tryInvokeOnTouched();
    }
  }

  @HostListener('keydown.enter')
  onEnterKeydown() {
    this.tryInvokeOnTouched();
  }

  private tryInvokeOnTouched() {
    if (this.ngControl?.control?.updateOn === 'blur') {
      (this.ngControl.valueAccessor as DefaultValueAccessor).onTouched?.();
    }
  }
}

import {
  Directive,
  ElementRef,
  HostListener,
  Renderer2,
  AfterViewInit,
  RendererStyleFlags2,
} from '@angular/core';
import { formatCurrency } from '@angular/common';

@Directive({
  selector: '[wesInputCurrencyMask]',
})
export class InputCurrencyMaskDirective implements AfterViewInit {
  private mask: HTMLSpanElement;

  constructor(
    private el: ElementRef<HTMLInputElement>,
    private renderer: Renderer2
  ) {
    this.createMaskElement();
  }

  ngAfterViewInit() {
    this.renderer.appendChild(
      this.mask,
      this.renderer.createText(this.el.nativeElement.value)
    );
    this.renderer.insertBefore(
      this.el.nativeElement.parentNode,
      this.mask,
      this.el.nativeElement
    );

    this.show();
  }

  @HostListener('focus', ['$event'])
  handleFocus(event: any) {
    this.hide();
  }

  @HostListener('blur', ['$event'])
  handleBlur(event: any) {
    this.show();
  }

  private createMaskElement() {
    this.mask = this.renderer.createElement('span');
    this.renderer.addClass(this.mask, 'wes-input-element');
    this.renderer.addClass(this.mask, 'wes-input-currency-mask');
    this.renderer.addClass(this.mask, 'd-none');
    this.renderer.setStyle(this.mask, 'position', 'absolute');
  }

  private show() {
    const value: number = parseFloat(this.el.nativeElement.value);
    const formatted = this.formatCurrencyGBP(value);
    this.mask.innerText = formatted;

    // tslint:disable-next-line: no-bitwise
    const flags = RendererStyleFlags2.DashCase | RendererStyleFlags2.Important;

    if (value) {
      this.renderer.addClass(this.mask, 'd-inline-block');
      this.renderer.removeClass(this.mask, 'd-none');
      this.renderer.setStyle(
        this.el.nativeElement,
        'color',
        'transparent',
        flags
      );
    }
  }

  private hide() {
    this.renderer.addClass(this.mask, 'd-none');
    this.renderer.removeClass(this.mask, 'd-inline-block');
    this.renderer.removeStyle(this.el.nativeElement, 'color');
  }

  private formatCurrencyGBP(value: number) {
    //Show 100.50 not 100.5 and 100 not 100.00
    const fraction = value % 1 === 0 ? '1.0-2' : '1.2-2';
    return formatCurrency(value, 'en-gb', '', 'GBP', fraction);
  }
}

import { Directive, ElementRef } from '@angular/core';

@Directive({
  selector: '[wesInvalidControlScrollContainer]',
})
export class InvalidControlScrollContainerDirective {
  readonly containerEl: HTMLElement = this.el.nativeElement;

  constructor(private el: ElementRef) {}
}

import {
  Directive,
  HostListener,
  ElementRef,
  Injector,
  Optional,
} from '@angular/core';
import { FormGroupDirective } from '@angular/forms';
import { fromEvent } from 'rxjs';
import { debounceTime, take } from 'rxjs/operators';

import { InvalidControlScrollContainerDirective } from './invalid-control-scroll-container.directive';

/**
 * https://medium.com/javascript-everyday/how-to-scroll-to-first-invalid-control-once-a-form-has-been-submitted-eb47d9fbc6e
 */
@Directive({
  selector: '[wesInvalidControlScroll]',
})
export class InvalidControlScrollDirective {
  private get containerEl(): HTMLElement | Window {
    return this.scrollContainerDir
      ? this.scrollContainerDir.containerEl
      : window;
  }

  constructor(
    private el: ElementRef,
    private formGroupDir: FormGroupDirective,
    @Optional()
    private scrollContainerDir: InvalidControlScrollContainerDirective
  ) {}

  @HostListener('ngSubmit') onSubmit() {
    if (this.formGroupDir.control.invalid) {
      this.scrollToFirstInvalidControl();
    }
  }

  private scrollToFirstInvalidControl() {
    const firstInvalidControl: HTMLElement = this.el.nativeElement.querySelector(
      '.ng-invalid'
    );

    this.containerEl.scroll({
      top: this.getTopOffset(firstInvalidControl),
      left: 0,
      behavior: 'smooth',
    });

    fromEvent(this.containerEl, 'scroll')
      .pipe(debounceTime(100), take(1))
      .subscribe(() => firstInvalidControl.focus());
  }

  private getTopOffset(controlEl: HTMLElement): number {
    const labelOffset = 50;
    const controlElTop = controlEl.getBoundingClientRect().top;

    if (this.scrollContainerDir && this.containerEl instanceof HTMLElement) {
      const containerTop = this.containerEl.getBoundingClientRect().top;
      const absoluteControlElTop = controlElTop + this.containerEl.scrollTop;

      return absoluteControlElTop - containerTop - labelOffset;
    } else {
      const absoluteControlElTop = controlElTop + window.scrollY;

      return absoluteControlElTop - labelOffset;
    }
  }
}

import { Directive, forwardRef, Provider } from '@angular/core';
import { CheckboxRequiredValidator, NG_VALIDATORS } from '@angular/forms';

export const MAT_CHECKBOX_REQUIRED_VALIDATOR: Provider = {
  provide: NG_VALIDATORS,
  useExisting: forwardRef(() => MatCheckboxRequiredValidator),
  multi: true,
};

/**
 * Validator for Material checkbox's required attribute in template-driven checkbox.
 * Current CheckboxRequiredValidator only work with `input type=checkbox` and does not
 * work with `mat-checkbox`.
 */
@Directive({
  selector: `mat-checkbox[required][formControlName],
               mat-checkbox[required][formControl], mat-checkbox[required][ngModel]`,
  providers: [MAT_CHECKBOX_REQUIRED_VALIDATOR],
})
export class MatCheckboxRequiredValidator extends CheckboxRequiredValidator {}

import {
  Directive,
  ElementRef,
  forwardRef,
  HostListener,
  Input,
} from '@angular/core';
import { MAT_INPUT_VALUE_ACCESSOR } from '@wesleyan-frontend/shared/ui-form-input';
import { NG_VALUE_ACCESSOR } from '@angular/forms';
import { formatNumber } from '@angular/common';

/**
 * https://medium.com/angular-in-depth/angular-material-matinput-control-with-thousands-separation-ebcbb7b027f4
 */
@Directive({
  selector: 'input[wesMatInputCommified]',
  providers: [
    {
      provide: MAT_INPUT_VALUE_ACCESSOR,
      useExisting: MatInputCommifiedDirective,
    },
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => MatInputCommifiedDirective),
      multi: true,
    },
  ],
})
export class MatInputCommifiedDirective {
  // tslint:disable-next-line:variable-name
  private _value: string | null;

  constructor(private elementRef: ElementRef<HTMLInputElement>) {}

  get value(): string | null {
    return this._value;
  }

  @Input('value')
  set value(value: string | null) {
    this._value = value;
    this.formatValue(value);
  }

  private formatValue(value: string | null) {
    if (value !== null) {
      this.elementRef.nativeElement.value = formatNumber(
        parseInt(value, 10),
        'en-gb'
      );
    } else {
      this.elementRef.nativeElement.value = '';
    }
  }

  private unFormatValue() {
    const value = this.elementRef.nativeElement.value;
    this._value = value.replace(/[^\d.-]/g, '');
    if (value) {
      this.elementRef.nativeElement.value = this._value;
    } else {
      this.elementRef.nativeElement.value = '';
    }
  }

  @HostListener('input', ['$event.target.value'])
  onInput(value) {
    this._value = value.replace(/[^\d.-]/g, '');
    this._onChange(this._value);
  }

  @HostListener('blur')
  _onBlur() {
    this.formatValue(this._value);
  }

  @HostListener('focus')
  onFocus() {
    this.unFormatValue();
  }

  _onChange(value: any): void {}

  writeValue(value: any) {
    this._value = value;
    this.formatValue(this._value);
    // this._onChange(this._value);
  }

  registerOnChange(fn: (value: any) => void) {
    this._onChange = fn;
  }

  registerOnTouched() {}
}

